@app.on_message(filters.new_chat_members)
async def wel__come(client: Client, message: Message):
	chatid= message.chat.id
	await client.send_message(text=f"ŸÜŸàÿ±ÿ™ Ÿäÿßÿß ÿ≠ÿ® {message.from_user.mention}\n‚îÇ \n‚îî·¥†·¥á ŸÅŸä {message.chat.title}",chat_id=chatid)
	
@app.on_message(filters.left_chat_member)
async def good_bye(client: Client, message: Message):
	chatid= message.chat.id
	await client.send_message(text=f"ŸÖÿ¥Ÿäÿ™ ŸÑŸäŸá ŸäŸàÿ≠ÿ¥ ŸäŸÑÿß ÿ®ÿ≥ŸÑÿßŸÖÿßÿ™ü•≤üëã\n‚îÇ \n‚îî·¥†·¥á  {message.from_user.mention} ",chat_id=chatid)
	
	

